mapper.select:

WHERE GROUP_CODE = '1000'
  AND CODE = #{code}
  AND ROWNUM = 1


WHERE PARENT_CODE = #{parentCode}
AND CODE = #{code}
AND ROWNUM = 1



WHERE PARENT_CODE = #{parentCode}


ORDER BY GROUP_CODE,
TO_NUMBER(DISPLAY)



    WHERE PARENT_CODE = #{parentCode}

ORDER BY GROUP_CODE,
TO_NUMBER(DISPLAY)

mapper.update:

UPDATE brewduckdb.BOARD
SET
  NTT_SJ = #{nttSj}
  ,NTT_CN = #{nttCn}
  ,ATCH_FILE_ID = #{atchFileId}
  ,UPDATE_ID = #{updateId}
  ,UPDATE_DATE = SYSDATE()
WHERE NTT_ID = #{nttId}
  AND BBS_ID = #{bbsId}
  AND DELETE_ID is null


UPDATE brewduckdb.BOARD
SET delete_id = #{deleteId}
,delete_date = SYSDATE()
WHERE NTT_ID = #{nttId}
AND BBS_ID = #{bbsId}
