select.trim:

    AND (NAME like CONCAT('%', #{name}, '%')
    OR korean_name like CONCAT('%', #{name}, '%'))



        AND ORIGIN != 'US' AND ORIGIN != 'DE' AND ORIGIN != 'UK' AND ORIGIN != 'BE' AND
        ORIGIN != 'CA'





        AND origin = #{origin}










        AND type = #{typeCode}










        AND supplier = #{supplier}





AND delete_id IS NULL

mapper.select:


AND a.recipe_seq = #{seq}
AND a.brewer = #{brewer}
AND b.delete_id IS NULL



WHERE delete_id IS NULL

    AND id = #{id}


    AND name = #{name}


    AND typeCode = #{typeCode}

mapper.update:

UPDATE FERMENTABLE


        korean_name = #{koreanName},


        version = #{version}


        type = #{type}


        amount = #{amount}


        yield = #{yield}


        color = #{color}


        add_after_boil = #{addAfterBoil}


        origin = #{origin}


        supplier = #{supplier}


        notes = #{notes}


        coarse_fine_diff = #{coarseFineDiff}


        moisture = #{moisture}


        diastatic_power = #{diastaticPower}


        protein = #{protein}


        max_in_batch = #{maxInBatch}


        recommend_mash = #{recommendMash}


        ibu_gal_per_lb = #{ibuGalPerLb}


        update_id = #{updateId},


        update_date = now()


WHERE name = #{name}


UPDATE FERMENTABLE
SET delete_id = #{deleteId}
,delete_date = now()
WHERE name = #{name}
