select.trim:

    AND (NAME like CONCAT('%', #{name}, '%')
    OR korean_name like CONCAT('%', #{name}, '%'))






        AND type =  #{type}










        AND use_for =  #{useFor}





AND delete_id IS NULL

mapper.select:


AND a.recipe_seq = #{seq}
AND a.brewer =  #{brewer}
AND b.delete_id IS NULL



WHERE delete_id IS NULL

    AND seq = #{seq}


    AND name = #{name}


    AND type = #{type}

mapper.update:

UPDATE brewduckdb.MISC


        korean_name = #{koreanName},


        version = #{version}


        type = #{type}


        misc_use = #{miscUse}


        time = #{time}


        amount = #{amount}


        amount_is_weight = #{amountIsWeight}


        use_for = #{useFor}


        notes = #{notes}


        update_id = #{updateId},


        update_date = now()


WHERE name = #{name}


UPDATE brewduckdb.MISC
   SET delete_id = #{deleteId}
      ,delete_date = now()
 WHERE name = #{name}
