mapper.select:


WHERE delete_id IS NULL

    AND brewer = #{brewer}

ORDER BY  seq desc

SELECT IFNULL(MAX(SEQ)+1,1) as seq
  FROM RECIPE
 WHERE brewer =  #{brewer}



WHERE delete_id IS NULL

    AND seq = #{seq}


    AND name = #{name}


    AND type = #{type}


    AND brewer = #{brewer}


    AND insert_id = #{insertId}

mapper.update:

UPDATE brewduckdb.RECIPE
   SET delete_id = #{deleteId}
      ,delete_date = now()
 WHERE seq  = #{seq}
   AND name = #{name}
